import { Component, OnInit } from '@angular/core';

@Component({
  selector: "app-destination-suggestion",
  templateUrl: "./destination-suggestion.component.html",
  styleUrls: ["./destination-suggestion.component.css"],
  standalone: false,
})
export class DestinationSuggestionComponent implements OnInit {
  public destinations: { name: string; image: string; rating: number }[];
  destinationString: string = "";

  placeholder1: any = [
    {
      name: "Paris",
      image:
        "https://media.istockphoto.com/id/1345426734/photo/eiffel-tower-paris-river-seine-sunset-twilight-france.jpg?s=612x612&w=0&k=20&c=I5rAH5d_-Yyag8F0CKzk9vzMr_1rgkAASGTE11YMh9A=",
      rating: 5,
    },
    {
      name: "Thailand",
      image:
        "https://www.shutterstock.com/image-photo/big-seated-buddha-statue-dhammakaya-260nw-2461072741.jpg",
      rating: 5,
    },
    {
      name: "Zurich",
      image:
        "https://plus.unsplash.com/premium_photo-1674297229805-e8a1877f9ca4?q=80&w=2070&auto=format&fit=crop&ixlib=rb-4.0.3&ixid=M3wxMjA3fDB8MHxwaG90by1wYWdlfHx8fGVufDB8fHx8fA%3D%3D",
      rating: 4,
    },
    {
      name: "Bali",
      image:
        "https://media.istockphoto.com/id/653953140/photo/hindu-temple-in-bali.jpg?s=612x612&w=0&k=20&c=ysj3S2kV1ZgCr4QZWDzjvHRowCI3-cR1xQNnqE8-BS4=",
      rating: 4,
    },
    {
      name: "New York",
      image:
        "https://t3.ftcdn.net/jpg/02/09/70/56/360_F_209705645_b78HGJI1i1mxqLwMYA7z1m3VvCxgxJFO.jpg",
      rating: 3,
    },
  ];

  placeholder2: any = [
    {
      name: "Tokyo",
      image:
        "data:image/jpeg;base64,",
      rating: 5,
    },
    {
      name: "Sydney",
      image:
        "data:image/jpeg;base64,",
      rating: 4,
    },
    {
      name: "London",
      image:
        "data:image/jpeg;base64,",
      rating: 3,
    },
    {
      name: "Dubai",
      image:
        "data:image/jpeg;base64,",
      rating: 2,
    },
    {
      name: "Rome",
      image:
        "data:image/jpeg;base64,",
      rating: 1,
    },
  ];

  constructor() {
    this.destinations = this.placeholder1;
  }

  ngOnInit(): void {
    const prevButton = document.querySelector(".prev");
    const nextButton = document.querySelector(".next");
    const cardsContainer = document.querySelector(".destination-container");

    if (prevButton != null && nextButton != null && cardsContainer != null) {
      prevButton.addEventListener("click", () => {
        cardsContainer.scrollBy({
          left: -300,
          behavior: "smooth",
        });
      });

      nextButton.addEventListener("click", () => {
        cardsContainer.scrollBy({
          left: 300,
          behavior: "smooth",
        });
      });
    }
  }

  scrollToSection(sectionId: string): void {
    const element = document.getElementById(sectionId);
    if (element) {
      const yOffset = -100;
      const y = element.getBoundingClientRect().top + window.scrollY + yOffset;

      window.scrollTo({ top: y, behavior: "smooth" });
    }
  }

  selectOtherDestination() {
    if (this.destinationString === "") {
      alert("A destination information must be provided");
    } else {
      if (this.destinations === this.placeholder1) {
        this.destinations = this.placeholder2;
      } else {
        this.destinations = this.placeholder1;
      }
    }
  }
}
